mapper.update:
		UPDATE PRODUCT_INFO 
        SET IS_ACCEPTED = #{is_accepted}
        WHERE PRODUCT_ID = #{product_id}

		UPDATE STOCK 
        SET IS_ACCEPTED = #{is_accepted}
        WHERE GUBUN = 'I'
        AND STOCK_ID = #{stock_id}

		UPDATE STOCK 
        SET IS_ACCEPTED = #{is_accepted}
        WHERE GUBUN = 'O' 
        AND STOCK_ID = #{stock_id}
